import React, { useState, useEffect, useCallback } from 'react';
import './App.css';

function App() {
  const [frames, setFrames] = useState([]);
  const [currentPage, setCurrentPage] = useState(0);
  const [loading, setLoading] = useState(false);
  const [uploading, setUploading] = useState(false);
  const [totalFrames, setTotalFrames] = useState(0);
  const [searchQuery, setSearchQuery] = useState('');
  const [error, setError] = useState(null);
  const [jobId, setJobId] = useState(null);
  const [dragActive, setDragActive] = useState(false);
  const framesPerPage = 12; // 3x4 grid

  // Get job ID from URL on component mount
  useEffect(() => {
    const pathParts = window.location.pathname.split('/');
    const urlJobId = pathParts[pathParts.length - 1];
    if (urlJobId && urlJobId.length === 36) { // UUID length
      setJobId(urlJobId);
    }
  }, []);

  // Fetch frames from the FastAPI backend
  const fetchFrames = useCallback(async () => {
    if (!jobId) return;

    try {
      setError(null);
      setLoading(true);
      const response = await fetch(`/api/frames/${jobId}`);
      if (response.ok) {
        const data = await response.json();
        setFrames(data.frames || []);
        setTotalFrames(data.total || 0);
      } else {
        throw new Error(`HTTP ${response.status}: ${response.statusText}`);
      }
    } catch (error) {
      console.error('Error fetching frames:', error);
      setError(error.message);
    } finally {
      setLoading(false);
    }
  }, [jobId]);

  // Poll for updates every 2 seconds when job exists
  useEffect(() => {
    if (jobId) {
      fetchFrames();
      const interval = setInterval(fetchFrames, 2000);
      return () => clearInterval(interval);
    }
  }, [fetchFrames, jobId]);

  // Handle file upload
  const handleFileUpload = async (file) => {
    if (!file) return;

    // Check file type
    const allowedTypes = ['video/mp4', 'video/avi', 'video/quicktime'];
    if (!allowedTypes.includes(file.type)) {
      setError('Only MP4, AVI, and MOV files are supported');
      return;
    }

    // Check file size (500MB)
    if (file.size > 500 * 1024 * 1024) {
      setError('File too large. Maximum size is 500MB');
      return;
    }

    setUploading(true);
    setError(null);

    try {
      const formData = new FormData();
      formData.append('video', file);

      const response = await fetch('/api/upload', {
        method: 'POST',
        body: formData,
      });

      if (response.ok) {
        const data = await response.json();
        setJobId(data.job_id);
        // Update URL without page reload
        window.history.pushState({}, '', `/${data.job_id}`);
      } else {
        const errorData = await response.json();
        throw new Error(errorData.detail || 'Upload failed');
      }
    } catch (error) {
      console.error('Upload error:', error);
      setError(error.message);
    } finally {
      setUploading(false);
    }
  };

  // Drag and drop handlers
  const handleDrag = useCallback((e) => {
    e.preventDefault();
    e.stopPropagation();
    if (e.type === "dragenter" || e.type === "dragover") {
      setDragActive(true);
    } else if (e.type === "dragleave") {
      setDragActive(false);
    }
  }, []);

  const handleDrop = useCallback((e) => {
    e.preventDefault();
    e.stopPropagation();
    setDragActive(false);

    if (e.dataTransfer.files && e.dataTransfer.files[0]) {
      handleFileUpload(e.dataTransfer.files[0]);
    }
  }, []);

  // File input handler
  const handleFileInput = (e) => {
    if (e.target.files && e.target.files[0]) {
      handleFileUpload(e.target.files[0]);
    }
  };

  // Calculate pagination
  const totalPages = Math.ceil(frames.length / framesPerPage);
  const startIndex = currentPage * framesPerPage;
  const endIndex = startIndex + framesPerPage;
  const currentFrames = frames.slice(startIndex, endIndex);

  // Navigation functions
  const goToPage = (page) => {
    setCurrentPage(Math.max(0, Math.min(page, totalPages - 1)));
  };

  const goToNextPage = () => goToPage(currentPage + 1);
  const goToPrevPage = () => goToPage(currentPage - 1);

  // Handle search
  const handleSearch = (e) => {
    e.preventDefault();
    setCurrentPage(0);
  };

  // Format file size
  const formatFileSize = (bytes) => {
    if (bytes === null || bytes === undefined) return 'Unknown';
    const sizes = ['Bytes', 'KB', 'MB', 'GB'];
    if (bytes === 0) return '0 Bytes';
    const i = Math.floor(Math.log(bytes) / Math.log(1024));
    return Math.round(bytes / Math.pow(1024, i) * 100) / 100 + ' ' + sizes[i];
  };

  // Upload area component
  const UploadArea = () => (
    <div 
      className={`upload-area ${dragActive ? 'drag-active' : ''}`}
      onDragEnter={handleDrag}
      onDragLeave={handleDrag}
      onDragOver={handleDrag}
      onDrop={handleDrop}
    >
      <div className="upload-content">
        <div className="upload-icon">üìÅ</div>
        <h3>Upload Video</h3>
        <p>Drag and drop your video file here, or click to browse</p>
        <p className="upload-info">Supported formats: MP4, AVI, MOV (max 500MB)</p>
        <input
          type="file"
          id="file-input"
          accept="video/mp4,video/avi,video/quicktime"
          onChange={handleFileInput}
          style={{ display: 'none' }}
        />
        <label htmlFor="file-input" className="upload-button">
          Choose File
        </label>
      </div>
    </div>
  );

  // Show upload area if no job ID
  if (!jobId) {
    return (
      <div className="app">
        <header className="header">
          <h1>Remove this Flash ‚ö°üé•</h1>
          <p className="description">
            The AI solution to remove flash from videos and replace individual frames in videos.
          </p>
        </header>

        {error && (
          <div className="error-message">
            Error: {error}
          </div>
        )}

        {uploading ? (
          <div className="uploading">
            <div className="uploading-spinner"></div>
            <p>Processing video... This may take a few minutes.</p>
          </div>
        ) : (
          <UploadArea />
        )}
      </div>
    );
  }

  // Show frames if job ID exists
  return (
    <div className="app">
      <header className="header">
        <h1>Remove this Flash ‚ö°üé•</h1>
        <p className="description">
          The AI solution to remove flash from videos and replace individual frames in videos.
        </p>
        <div className="info">
          <span>Job ID: {jobId}</span>
          <span>Total frames: {totalFrames}</span>
          <span>Page {currentPage + 1} of {totalPages}</span>
        </div>

        {/* Search form */}
        <form onSubmit={handleSearch} className="search-form">
          <input
            type="text"
            value={searchQuery}
            onChange={(e) => setSearchQuery(e.target.value)}
            placeholder="Search frames..."
            className="search-input"
          />
          <button type="submit" className="search-button">
            Search
          </button>
          {searchQuery && (
            <button 
              type="button" 
              onClick={() => {
                setSearchQuery('');
                setCurrentPage(0);
              }}
              className="clear-button"
            >
              Clear
            </button>
          )}
        </form>
      </header>

      {error && (
        <div className="error-message">
          Error: {error}
        </div>
      )}

      {loading && frames.length === 0 ? (
        <div className="loading">Loading frames...</div>
      ) : (
        <>
          <div className="frames-grid">
            {currentFrames.map((frame, index) => (
              <div key={frame.name} className="frame-item">
                <img 
                  src={`/api/frames/${jobId}/${encodeURIComponent(frame.name)}`} 
                  alt={frame.name}
                  onError={(e) => {
                    e.target.style.display = 'none';
                    e.target.nextSibling.style.display = 'block';
                  }}
                />
                <div className="frame-error" style={{ display: 'none' }}>
                  Failed to load
                </div>
                <div className="frame-info">
                  <div className="frame-name">{frame.name}</div>
                  {frame.size && (
                    <div className="frame-size">{formatFileSize(frame.size)}</div>
                  )}
                </div>
              </div>
            ))}
          </div>

          {totalPages > 1 && (
            <div className="pagination">
              <button 
                onClick={goToPrevPage} 
                disabled={currentPage === 0}
                className="nav-button"
              >
                Previous
              </button>

              <div className="page-numbers">
                {Array.from({ length: totalPages }, (_, i) => (
                  <button
                    key={i}
                    onClick={() => goToPage(i)}
                    className={`page-button ${currentPage === i ? 'active' : ''}`}
                  >
                    {i + 1}
                  </button>
                ))}
              </div>

              <button 
                onClick={goToNextPage} 
                disabled={currentPage === totalPages - 1}
                className="nav-button"
              >
                Next
              </button>
            </div>
          )}

          {frames.length === 0 && !loading && (
            <div className="no-frames">
              {searchQuery 
                ? `No frames found matching "${searchQuery}"`
                : "No frames found. Video processing may still be in progress."
              }
            </div>
          )}
        </>
      )}
    </div>
  );
}

export default App;
