import React, { useState, useCallback, useEffect, useMemo } from 'react';
import { useParams, useNavigate, Routes, Route } from 'react-router-dom';
import './App.css';

function UploadInterface() {
  const [selectedFile, setSelectedFile] = useState(null);
  const [uploading, setUploading] = useState(false);
  const [uploadProgress, setUploadProgress] = useState(0);
  const [uploadStatus, setUploadStatus] = useState('');
  const [uploadSpeed, setUploadSpeed] = useState(0);
  const [uploadStartTime, setUploadStartTime] = useState(null);
  const [isUploading, setIsUploading] = useState(false);
  const [repoUuid, setRepoUuid] = useState(null);
  const [isProcessing, setIsProcessing] = useState(false);
  const [processingStatus, setProcessingStatus] = useState('');
  const [totalFrames, setTotalFrames] = useState(0);
  const navigate = useNavigate();

  const handleFileSelect = (file) => {
    console.log('ðŸ“‚ File selected:', {
      name: file?.name,
      type: file?.type,
      size: file?.size
    });

    if (file && file.type.startsWith('video/')) {
      console.log('âœ… Valid video file selected');
      setSelectedFile(file);
      setUploadStatus('');
      // Automatically start upload when file is selected
      handleUpload(file);
    } else {
      console.warn('âŒ Invalid file type selected:', file?.type);
      setUploadStatus('Please select a valid video file.');
      setSelectedFile(null);
    }
  };

  const handleFileInputChange = (e) => {
    console.log('ðŸ–±ï¸ File input change event');
    const file = e.target.files[0];
    handleFileSelect(file);
  };

  const handleUpload = useCallback(async (file) => {
    if (!file) return;

